mapper.update:
UPDATE company
SET name    = #{name},
    biz_no  = #{bizNo},
    address = #{address},
    phone   = #{phone}
WHERE company_id = #{companyId}

mapper.select:
SELECT CASE WHEN deleted_at IS NULL THEN 1 ELSE 0 END
FROM company
WHERE company_id = #{companyId}

SELECT CASE WHEN COUNT(1) > 0 THEN 1 ELSE 0 END
FROM COMPANY
WHERE biz_no = #{bizNo}

    AND company_id != #{excludeId}

AND deleted_at IS NULL

SELECT CASE WHEN COUNT(1) > 0 THEN 1 ELSE 0 END FROM COMPANY WHERE LOWER(name) = LOWER(#{name})
AND company_id != #{excludeId}
AND deleted_at IS NULL
