mapper.select:
SELECT level_no
FROM position
WHERE position_id = #{positionId}

mapper.update:
UPDATE position
SET name = #{name}
WHERE position_id = #{positionId}

UPDATE position
SET level_no = #{levelNo}
WHERE position_id = #{positionId}

UPDATE position
SET level_no = level_no - 1
WHERE level_no between #{oldLevelNo} + 1 AND #{newLevelNo}

mapper.select:
SELECT CASE WHEN COUNT(1) > 0 THEN 1 ELSE 0 END
FROM position
WHERE position_id = #{positionId}
