mapper.select:
SELECT refresh_token_id
FROM refresh_token
WHERE token = #{token}

mapper.update:
UPDATE refresh_token
SET token = #{token}
WHERE erp_account_id = #{erpAccountId}

mapper.select:
SELECT CASE WHEN COUNT(1) > 0 THEN 1 ELSE 0 END
FROM refresh_token
WHERE erp_account_id = #{erpAccountId}
