SELECT (SELECT NVL(SUM(s.on_hand_quantity), 0) FROM item i JOIN stock s ON s.item_id = i.item_id WHERE i.item_id = #{filter.itemId} AND i.deleted_at IS NULL AND
AND
) AS onHandQuantity,
(SELECT s.warehouse FROM stock s JOIN item i2 ON i2.item_id = s.item_id WHERE i2.item_id = #{filter.itemId} AND i2.deleted_at IS NULL AND
AND
) AS warehouse,
(SELECT COUNT(DISTINCT inb.inbound_id) FROM inbound inb JOIN purchase p ON p.purchase_id = inb.purchase_id JOIN purchase_item pi ON pi.purchase_id = p.purchase_id JOIN item i ON i.item_id = pi.item_id WHERE i.item_id = #{filter.itemId} AND inb.inbound_date >= #{filter.startDate}
AND inb.inbound_date <= #{filter.endDate}
AND UPPER(inb.code) LIKE '%' || UPPER(#{filter.code}) || '%'
AND inb.status = 'ACTIVE'
AND inb.status = 'CANCELED'
AND i.deleted_at IS NULL AND
AND
AND
) AS totalInboundCount,
(SELECT COUNT(DISTINCT outb.outbound_id) FROM outbound outb JOIN orders o ON o.order_id = outb.order_id JOIN order_item oi ON oi.order_id = o.order_id JOIN item i ON i.item_id = oi.item_id WHERE i.item_id = #{filter.itemId} AND outb.outbound_date >= #{filter.startDate}
AND outb.outbound_date <= #{filter.endDate}
AND UPPER(outb.code) LIKE '%' || UPPER(#{filter.code}) || '%'
AND outb.status = 'ACTIVE'
AND outb.status = 'CANCELED'
AND i.deleted_at IS NULL AND
AND
AND
) AS totalOutboundCount FROM dual
